mapper.select:
select us.id, us.username,us.password,us.status,us.role_id as roleId, ur.name as roleName,us.name, us.created_time as createdTime, us.created_by as createdBy, us.updated_time as updatedTime, us.updated_by as updatedBy, cu.username as createdUser, uu.username as updatedUser from `user` us left join user_role ur on ur.id=us.role_id left JOIN `user` cu on cu.id=us.created_by LEFT JOIN `user` uu on uu.id=us.updated_by where 1=1 and us.is_deleted != 1
and instr( us.username,#{username}) > 0
and instr( us.name,#{name}) > 0
and us.status = #{status}
order by us.updated_time desc, us.created_time desc limit #{start},#{pageSize}
